/// This module contains the secp256k1 constants for the class groups protocol.
/// NOTE: This is a temporary solution until the class groups DKG is complete.
/// Todo (#312): Remove this module and use the class groups DKG to generate the constants.
use group::secp256k1;

const PUBLIC_PARAMETERS : &str = "";
const DECRYPTION_KEY : &str = "";
const ENCRYPTION_KEY : &str = "";

pub fn protocol_public_parameters() -> anyhow::Result<
    twopc_mpc::class_groups::ProtocolPublicParameters<
        { secp256k1::SCALAR_LIMBS },
        { twopc_mpc::secp256k1::class_groups::DISCRIMINANT_LIMBS },
        secp256k1::GroupElement,
    >,
> {
    let protocol_public_parameters = hex::decode(&PUBLIC_PARAMETERS)?;
    let protocol_public_parameters = bcs::from_bytes(&protocol_public_parameters)?;

    Ok(protocol_public_parameters)
}
