#[derive(Clone, Debug)]
pub enum Hash {
    KECCAK256 = 0,
    SHA256 = 1
}

impl From<u8> for Hash {
    fn from(value: u8) -> Self {
        match value  {
            0 => Self::KECCAK256,
            1 => Self::SHA256,
            _ => panic!()
        }
    }
}

impl From<Hash> for u8 {
    fn from(value: Hash) -> Self {
        match value  {
            Hash::KECCAK256 => 0,
            Hash::SHA256 => 1
        }
    }
}

